CREATE Function [dbo].[GetCharNumber]
	(
	  @String NVARCHAR(Max),
	  @Char nvarchar(1)
	)
RETURNS int
AS
	BEGIN
		DECLARE @i int
		declare @num int
		
		set @i=1;
		set @num=0;
		while @i<=LEN(@String)
		begin
			if SUBSTRING(@String,@i,1)=@Char
			begin
				set @num=@num+1;
			end
			set @i=@i+1
		end
		return @num;
	END
	

