CREATE FUNCTION [dbo].[GetDebugUserSystemFunctionTree]
(@user_id UNIQUEIDENTIFIER,@org_id UNIQUEIDENTIFIER,@product_id UNIQUEIDENTIFIER,@system_name NVARCHAR(100),@base_url NVARCHAR(1024))
RETURNS @UserProductFunctionTree  Table
( 
    user_id                 UNIQUEIDENTIFIER,
    user_name				NVARCHAR(100),
    org_id					UNIQUEIDENTIFIER,
    org_name				NVARCHAR(100),
    product_id				UNIQUEIDENTIFIER,
    product_name			NVARCHAR(100),
    permission_mode         NVARCHAR(100),
    domain_id				UNIQUEIDENTIFIER,
    domain_name				NVARCHAR(100),
    domain_sort_code		INT,
    system_id				UNIQUEIDENTIFIER,
    system_name             NVARCHAR(100),   
    system_description		NVARCHAR(250),
    instance_name			NVARCHAR(20),
    system_type				NVARCHAR(30),
    system_sort_code        INT,
    function_id             UNIQUEIDENTIFIER,
    parent_function_id      UNIQUEIDENTIFIER,
    node_level_code       NVARCHAR(100),
    tree                    NVARCHAR(max),
    node_type               NVARCHAR(30),    
    function_code           NVARCHAR(30),
    function_name           NVARCHAR(100),
    function_description    NVARCHAR(250),
    assembly				NVARCHAR(100),
    form_class				NVARCHAR(100),
    navigate_url            NVARCHAR(max),
    external_navigate_url	NVARCHAR(max),
    target                  NVARCHAR(50),
    instance_type           NVARCHAR(30),
    is_public				INT,
    system_icon_name        NVARCHAR(50),
    custom_icon_url         NVARCHAR(max),
    function_sort_code		INT
)
As
begin
	declare
			@user_name nvarchar(100),
			@org_name nvarchar(100)
	    
		set @user_name=(select [user_name] from view_base_user where [user_id]=@user_id and active=1);
		set @org_name=(select name from base_org where id=@org_id and active=1);
	    
    WITH FunctionTree AS(
        select distinct p.name As Product,
			d.id As DomainId,
			d.name As Domain,
			d.sort_code As domain_sort_code,
			s.id As SystemId,
			s.name As System,
			s.description As system_description,
			s.instance_name,
			s.system_type,
			s.sort_code As system_sort_code,
			f.id,
			f.parent_function_id,
			f.node_level_code,
			space(dbo.GetCharNumber(f.node_level_code,'.')*4)+f.name Tree, 
			f.node_type,
			f.code,
			f.name,
            f.description As function_description,
            f.assembly,
            f.form_class,
            s.base_url,
            s.external_base_url,
            f.navigate_url, 
            f.target,
            f.instance_type,
            f.is_public,
            f.system_icon_name, 
            f.custom_icon_url,
            f.sort_code As function_sort_code
        from base_product p WITH (NOLOCK),
            base_domain d WITH (NOLOCK),
            base_system s WITH (NOLOCK),
            base_function f WITH (NOLOCK)
        where p.id=d.product_id
        and d.id=s.domain_id
        and s.id=f.system_id        
        and p.active=1
        and d.active=1
        and s.active=1
        and f.active=1
        and s.org_id=@org_id
        and s.name=@system_name
        and p.id=@product_id      
    )  
    insert into @UserProductFunctionTree
    SELECT @user_id,
		   @user_name,
		   @org_id,
		   @org_name,
		   @product_id,
		   Product,
           'DebugMode',
           DomainId,
		   Domain,
		   domain_sort_code,
		   SystemId,
           System,
           system_description,
           instance_name,
           system_type,
           system_sort_code,
           id,
           parent_function_id,
           node_level_code,
           Tree,
           node_type,
           code,
           name,
           function_description,
           assembly,
           form_class,
           isnull((select navigate_url where navigate_url like 'http://%'),@base_url+navigate_url),
           isnull((select navigate_url where navigate_url like 'http://%'),@base_url+navigate_url),           
           target,
           instance_type,
           is_public,
           system_icon_name,
           custom_icon_url,
           function_sort_code
    FROM   FunctionTree
    ORDER  BY domain_sort_code,
              system_sort_code,
              node_level_code 
    return
end


