CREATE FUNCTION [dbo].[GetDefaultPermissionMode]
    (
      @org_id UNIQUEIDENTIFIER ,
      @product_id UNIQUEIDENTIFIER
    )
RETURNS NVARCHAR(50)
    BEGIN
        DECLARE @permission_mode NVARCHAR(50)
        SELECT  @permission_mode = name
        FROM    base_permission_mode WITH ( NOLOCK )
        WHERE   is_default = 1
                AND active = 1
                AND org_id = @org_id
                AND product_id = @product_id
    
        RETURN @permission_mode
    END
