CREATE FUNCTION [dbo].[GetDepartmentIDFromUserID]
(@user_id UNIQUEIDENTIFIER)
RETURNS @DepartmentIDList  Table
(     
    department_id           	UNIQUEIDENTIFIER  
)
As
BEGIN
    WITH department_id_list As (
        select d.id As department_id,d.parent_deptartment_id 
            from base_staff_department sd WITH (NOLOCK),
                base_staff s WITH (NOLOCK),
                base_department d WITH (NOLOCK)
        where sd.staff_id=s.id
          and sd.department_id=d.id
          and sd.active=1
          and s.active=1
          and d.active=1          
          and s.id=@user_id
         union all
         select d.id,d.parent_deptartment_id 
            from base_department d  WITH (NOLOCK)
                inner join department_id_list dil
                    on d.id=dil.parent_deptartment_id
         where d.active=1       
    )
  insert into @DepartmentIDList select department_id from department_id_list   
  RETURN 
END
