CREATE FUNCTION [dbo].[GetRoleIDFromUserID]
(@user_id UNIQUEIDENTIFIER)
RETURNS @RoleIDList  Table
(     
    role_id           	UNIQUEIDENTIFIER  
)
As
BEGIN
    WITH user_role_id_list As (
        select ru.role_id,r.parent_role_id 
           from base_role_user ru WITH (NOLOCK),
            base_role r WITH (NOLOCK),
            view_base_user u WITH (NOLOCK)
        where ru.role_id=r.id
          and ru.user_id=u.user_id
          and ru.active=1
          and r.active=1
          and u.active=1
          and u.user_id=@user_id
         union all
         select r.id,r.parent_role_id 
            from base_role r  WITH (NOLOCK)
           inner join user_role_id_list ril
         on r.id=ril.parent_role_id
         where r.active=1       
    ),
    dept_role_id_list As (
        select distinct role_id_list.role_id 
          from dbo.GetDepartmentIDFromUserID(@user_id) As dept_id_list
            cross apply dbo.GetRoleIDFromDepartmentID(dept_id_list.department_id) as role_id_list
    )
  insert into @RoleIDList 
    select role_id from user_role_id_list
    union
    select role_id from dept_role_id_list
    union
    select '00000000-0000-0000-0000-000000000002' --Everyone system role
  RETURN 
END
