CREATE FUNCTION [dbo].[GetUserProductFunctionTree]
(@user_id UNIQUEIDENTIFIER,@org_id UNIQUEIDENTIFIER,@product_id UNIQUEIDENTIFIER)
RETURNS @UserProductFunctionTree  Table
( 
    user_id                UNIQUEIDENTIFIER,
    permission_mode         NVARCHAR(100),
    domain_name             NVARCHAR(100),
    system_name             NVARCHAR(100),    
    node_level_code         NVARCHAR(100),
    tree                    NVARCHAR(max),
    parent_function_id      UNIQUEIDENTIFIER,
    function_id             UNIQUEIDENTIFIER,
    node_type               NVARCHAR(30),    
    function_code           NVARCHAR(30),
    function_name           NVARCHAR(100),
    function_description    NVARCHAR(250),
    navigate_url            NVARCHAR(1024),
    target                  NVARCHAR(50),
    instance_type           NVARCHAR(30),
    system_icon_name        NVARCHAR(50),
    custom_icon_url         NVARCHAR(1024),
    domain_sort_code        INT,
    system_sort_code        INT
)
As
begin
    /*
    declare @count  int

    select @count=count(*) from cache_user_product_permission
        where user_id=@user_id
         and org_id=@org_id
         and product_id=@product_id
    if @count=0
    begin
        EXECUTE usp_GenerateUserProductPermissionCache @user_id,@org_id,@product_id
    end
    insert into @UserProductFunctionTree
        select user_id, permission_mode, domain, system, node_level_code,tree, 
            parent_function_id, function_id, node_type, function_code, 
            function_name, function_description, navigate_url, target, instance_type, 
            system_icon_name, custom_icon_url, domain_sort_code, system_sort_code
        from cache_user_product_permission
            order by domain,system,node_level_code
    return    
    */
WITH user_permission As
    (
        select distinct m.description PermissionMode,d.name As Domain,s.name As System,f.node_level_code,space((node_level-1)*4)+f.description Tree, f.parent_function_id,f.id,f.node_type,f.code,f.name,
            f.description,s.base_url,f.navigate_url, f.target,f.instance_type,f.system_icon_name, 
            f.custom_icon_url,d.sort_code As domain_sort_code,s.sort_code As system_sort_code
        from base_permission perm WITH (NOLOCK),
            base_permission_mode m WITH (NOLOCK),
            base_product p,base_domain d WITH (NOLOCK),
            base_system s,base_function f WITH (NOLOCK)
        where perm.permission_mode_id=m.id
        and perm.resource_id=f.id
        and f.system_id=s.id
        and s.domain_id=d.id
        and d.product_id=p.id
        and m.org_id=@org_id
        and m.product_id=@product_id
        and m.is_default=1
        and perm.active=1
        and m.active=1
        and p.active=1
        and d.active=1
        and s.active=1
        and f.active=1
        and perm.active=1
        and perm.resource_org_id=@org_id
        and p.id=@product_id
        and (
                (perm.subject_category='User'
                    and perm.resource_category='Function'        
                    and perm.subject_id=@user_id
                )
                or
                (perm.subject_category='Role'
                    and perm.resource_category='Function'
                    and perm.subject_id in(select role_id from dbo.GetRoleIDFromUserID(@user_id))
                )
                or
                (perm.subject_category='Department'
                    and perm.resource_category='Function'
                    and perm.subject_id in(select department_id from dbo.GetDepartmentIDFromUserID(@user_id))
                )
            )
    ),FunctionTree As(
        select user_permission.* from user_permission
        where parent_function_id is null
        union all
        select user_permission.* from user_permission
        inner join FunctionTree d
        on d.id=user_permission.parent_function_id
    )
    insert into @UserProductFunctionTree
    SELECT @user_id,
           PermissionMode,
           Domain,
           System,
           node_level_code,
           Tree,
           parent_function_id,
           id,
           node_type,
           code,
           name,
           description,
           isnull((select navigate_url where navigate_url like 'http://%'),base_url+navigate_url),           
           target,
           instance_type,
           system_icon_name,
           custom_icon_url,
           domain_sort_code,
           system_sort_code
    FROM   FunctionTree
    union
    SELECT distinct @user_id,
           (select top 1 name from base_permission_mode where is_default=1 and active=1) As PermissionMode,
           d.name As Domain,
           s.name As System,
           f.node_level_code,
           space((f.node_level-1)*4)+f.description Tree,
           f.parent_function_id,
           f.id,
           f.node_type,
           f.code,
           f.name,
           f.description,
           isnull((select f.navigate_url where f.navigate_url like 'http://%'),s.base_url+f.navigate_url),           
           f.target,
           f.instance_type,
           f.system_icon_name,
           f.custom_icon_url,
           d.sort_code As domain_sort_code,
           s.sort_code As system_sort_code
    FROM base_function f  WITH (NOLOCK),
        base_system s  WITH (NOLOCK),
        base_role r  WITH (NOLOCK), 
        base_role_user ru  WITH (NOLOCK),
        base_domain d  WITH (NOLOCK)
    where f.system_id=s.id
        and s.admin_role_id=r.id
        and r.id=ru.role_id
        and s.domain_id=d.id
        and ru.user_id=@user_id
        and d.product_id=@product_id
        and s.org_id=@org_id
        and f.active=1
        and s.active=1
        and r.active=1
        and ru.active=1
        and d.active=1
    return
end
