CREATE FUNCTION [dbo].[HasOperationPermission]
(
    @user_id        UNIQUEIDENTIFIER,
    @function_id    UNIQUEIDENTIFIER,
    @operation_name NVARCHAR(100)
)
RETURNS int
As
begin
    declare @count int,        
        @operation_id UNIQUEIDENTIFIER
                
        select @operation_id=op.id from base_operation op INNER JOIN base_function f
            on op.function_id=f.id
         where op.name=@operation_name                    
                and op.active=1
                and f.active=1
        if @operation_id is null
        begin
            return cast('[Function ID='''+cast(@function_id as varchar(50))+''',Opeation Name ''' + @operation_name +'''] not found!' As int);                       
        end 
    
        select @count=count(*)
        from base_permission perm WITH (NOLOCK),
            base_permission_mode m WITH (NOLOCK),
            base_product p WITH (NOLOCK),
            base_domain d WITH (NOLOCK),
            base_system s WITH (NOLOCK),
            base_function f WITH (NOLOCK),
            base_operation op WITH (NOLOCK)
        where perm.permission_mode_id=m.id
        and perm.resource_id=f.id
        and f.system_id=s.id
        and s.domain_id=d.id
        and d.product_id=p.id
        and f.id=op.function_id
        and m.is_default=1
        and perm.active=1
        and m.active=1
        and p.active=1
        and d.active=1
        and s.active=1
        and f.active=1
        and op.active=1
        and perm.active=1
        and perm.resource_id =@operation_id        
        and (
                (perm.subject_category='User'
                    and perm.resource_category='Opeation'
                    and perm.subject_id=@user_id
                )
                or
                (perm.subject_category='Role'
                    and perm.resource_category='Operation'
                    and perm.subject_id in(select role_id from dbo.GetRoleIDFromUserID(@user_id))
                )
                or
                (perm.subject_category='Department'
                    and perm.resource_category='Operation'
                    and perm.subject_id in(select department_id from dbo.GetDepartmentIDFromUserID(@user_id))
                )
            )
   return @count
end
