	Create Function [dbo].[Split]
	(
		@String NVARCHAR(Max),
		@Char NVARCHAR(1)
	)
	RETURNS @SplitString Table(split NVARCHAR(Max))
	AS
	BEGIN
	while(CHARINDEX(@Char,@String)<>0)
		begin
			insert @SplitString(split) values (SUBSTRING(@String,1,CHARINDEX(@Char,@String)-1))	
			set @String=STUFF(@String,1,CHARINDEX(@Char,@String),'')
		end	
		insert @SplitString(split) values (@String)
		return
	END
