CREATE VIEW [dbo].[view_base_user]
AS
    SELECT  base_staff.id user_id ,
            base_staff.org_id ,
            base_staff.user_name ,
            base_staff.real_name ,
            base_staff.employee_id ,
            base_staff.employee_no ,
            '' AS password ,
