            base_staff.extension ,
            base_staff.department_code ,
            base_staff.title ,
            base_staff.entry_date ,
            base_staff.active ,
            base_staff.gender ,
            base_staff.computer_name ,
            1 AS is_staff
    FROM    base_staff
    WHERE   base_staff.active = 1
    UNION
    SELECT  base_user.id user_id ,
            base_user.org_id ,
