            base_user.telphone ,
            base_user.company ,
            '' AS extension ,
            base_user.department ,
            base_user.title ,
            NULL AS entry_date ,
            base_user.active ,
            base_user.gender ,
            '' AS computer_name ,
            0 AS is_staff
    FROM    base_user
    WHERE   base_user.active = 1
