CREATE VIEW [dbo].[view_base_resource]
AS
    SELECT  module.id resource_id ,
            module.name resource_name ,
            N'Function' resource_category
    FROM    base_function module
    WHERE   module.active = 1
    UNION
    SELECT  op.id resource_id ,
            op.name resource_name ,
            N'Operation' AS resource_category
    FROM    base_operation op
    WHERE   op.active = 1
    UNION
    SELECT  system.id resource_id ,
            system.name resource_name ,
            N'System' AS resource_category
    FROM    base_system system
    WHERE   system.active = 1
    UNION
    SELECT  col.id resource_id ,
            col.name resource_name ,
            N'SensitiveColumn' AS resource_category
    FROM    base_data_table_column col
    WHERE   col.is_sensitive = 1
            AND col.active = 1
