/****** Object:  StoredProcedure [dbo].[usp_GenerateUserProductPermissionCache]    Script Date: 2017-9-8 9:21:18 ******/
SET ANSI_NULLS ON
GO
SET QUOTED_IDENTIFIER ON
GO
CREATE PROCEDURE [dbo].[usp_GenerateUserProductPermissionCache]
(
    @user_id uniqueidentifier,
    @org_id uniqueidentifier,
    @product_id uniqueidentifier
)
AS 
    declare
		@user_name nvarchar(100),
		@org_name nvarchar(100),
		@product_name nvarchar(100),
		@permission_mode_id uniqueidentifier,
        @permission_mode nvarchar(50)
    
    set @user_name=(select [user_name] from view_base_user where [user_id]=@user_id and active=1);
    set @org_name=(select name from base_org where id=@org_id and active=1);
    set @product_name=(select name from base_product where id=@product_id and active=1);
    
	select @permission_mode_id=id,@permission_mode=name 
	from base_permission_mode WITH ( NOLOCK )
	where is_default = 1
      and active = 1
      and org_id = @org_id
      and product_id = @product_id;

    delete from cache_user_product_permission 
     where [user_id]=@user_id
      and org_id=@org_id
      and product_id=@product_id
      and permission_mode_id=@permission_mode_id;

    WITH user_permission As
    (
        select distinct p.name Product,
			pm.id PermissionModeId,
			pm.name PermissionMode,
			d.id As DomainId,
			d.name As Domain,
			d.sort_code As domain_sort_code,
			s.id As SystemId,
			s.name As System,
			s.description As system_description,
			s.instance_name,
			s.system_type,
			s.sort_code As system_sort_code,
			f.id,
			f.parent_function_id,
			f.node_level_code,
			space(dbo.GetCharNumber(f.node_level_code,'.')*4)+f.name Tree, 
			f.node_type,
			f.code,
			f.name,
            f.description As function_description,
            f.assembly,
            f.form_class,
            s.base_url,
            s.external_base_url,
            f.navigate_url, 
            f.target,
            f.instance_type,
            f.is_public,
            f.system_icon_name, 
            f.custom_icon_url,
            f.sort_code As function_sort_code
        from base_permission perm,base_permission_mode pm,
            base_product p,base_domain d,base_system s,base_function f
        where perm.permission_mode_id=pm.id
        and perm.resource_id=f.id
        and f.system_id=s.id
        and s.domain_id=d.id
        and d.product_id=p.id
        and pm.org_id=@org_id
        and pm.product_id=@product_id
        and pm.is_default=1        
        and pm.active=1
        and perm.active=1
        and p.active=1
        and d.active=1
        and s.active=1
        and f.active=1
        and perm.active=1
        and perm.resource_org_id=@org_id
        and p.id=@product_id
        and (
                (perm.subject_category='User'
                    and perm.resource_category='Function'        
                    and perm.subject_id=@user_id
                )
                or
                (perm.subject_category='Role'
                    and perm.resource_category='Function'
                    and perm.subject_id in(select role_id from dbo.GetRoleIDFromUserID(@user_id))
                )
                or
                (perm.subject_category='Department'
                    and perm.resource_category='Function'
                    and perm.subject_id in(select department_id from dbo.GetDepartmentIDFromUserID(@user_id))
                )
            )
    ),FunctionTree As(
        select user_permission.* from user_permission
        where parent_function_id is null
        union all
        select user_permission.* from user_permission
        inner join FunctionTree d
        on d.id=user_permission.parent_function_id
    )
    insert into cache_user_product_permission
    (
		[user_id],
		[user_name],
		org_id,
		org_name,
		product_id,
		product_name,
		permission_mode_id,
		permission_mode,
		domain_id,
		domain_name,
		domain_sort_code,
		system_id,
		system_name,
		system_description,
		instance_name,
		system_type,
		system_sort_code,
		function_id,
		parent_function_id,
		node_level_code,
		tree,
		node_type,
		function_code,
		function_name,
		function_description,
		assembly,
        form_class,
		navigate_url,
		external_navigate_url,
		target,
		instance_type,
		is_public,
		system_icon_name,
		custom_icon_url,
		function_sort_code
    )    
    SELECT distinct @user_id [user_id],@user_name [user_name],
		   @org_id org_id,
		   @org_name org,
		   @product_id product_id,
		   Product,
		   PermissionModeId,
           PermissionMode,
           DomainId,
           Domain,
           domain_sort_code,
           SystemId,
           System,
           system_description,
           instance_name,
           system_type,
           system_sort_code,
           id function_id,
           parent_function_id,
           node_level_code,
           Tree,       
           node_type,
           code,
           name,
           function_description,
           assembly,
		   form_class,
           isnull((select navigate_url where navigate_url like 'http://%' or navigate_url like 'https://'),base_url+navigate_url) navigate_url, 
           isnull((select navigate_url where navigate_url like 'http://%' or navigate_url like 'https://'),external_base_url+navigate_url) external_navigate_url, 
           target,
           instance_type,
           is_public,
           system_icon_name,
           custom_icon_url,
           function_sort_code      
    FROM   FunctionTree
        WHERE name<>'Hidden Functions'
    union
    SELECT @user_id,@user_name,@org_id,@org_name,@product_id,@product_name,
		   @permission_mode_id As PermissionModeId,
           @permission_mode As PermissionMode,
           d.id As DomainId,
           d.name As Domain,
           d.sort_code As domain_sort_code,
           s.id As SystemId,
           s.name As System,
           s.description As system_description,
		   s.instance_name,
		   s.system_type,
           s.sort_code As system_sort_code,
           f.id,
           f.parent_function_id,
           f.node_level_code,
           space(dbo.GetCharNumber(f.node_level_code,'.')*4)+f.name Tree,
           f.node_type,
           f.code,
           f.name,
           f.description,
           f.assembly,
           f.form_class,
           isnull((select f.navigate_url where f.navigate_url like 'http://%' or navigate_url like 'https://'),s.base_url+f.navigate_url),
           isnull((select f.navigate_url where f.navigate_url like 'http://%' or navigate_url like 'https://'),s.external_base_url+f.navigate_url),           
           f.target,
           f.instance_type,
           f.is_public,
           f.system_icon_name,
           f.custom_icon_url,
           f.sort_code As function_sort_code
    FROM base_function f,base_system s,base_role r, base_role_user ru,base_domain d
    where f.system_id=s.id
        and s.admin_role_id=r.id
        and r.id=ru.role_id
        and s.domain_id=d.id        
        and ru.user_id=@user_id
        and d.product_id=@product_id
        and s.org_id=@org_id        
        and f.name<>'Hidden Functions'
        and f.active=1
        and s.active=1
        and r.active=1
        and ru.active=1
        and d.active=1
GO
