CREATE PROCEDURE [dbo].[usp_GetUserProductPermission]
    (
      @user_id UNIQUEIDENTIFIER ,
      @org_id UNIQUEIDENTIFIER ,
      @product_id UNIQUEIDENTIFIER
    )
AS 
    DECLARE @count INT ,
        @permission_mode NVARCHAR(50),
        @permission_mode_id UNIQUEIDENTIFIER,
        @user_name NVARCHAR(100)
        
    SET @user_name=(select [user_name] from view_base_user where [user_id]=@user_id and active=1);
    select @permission_mode_id=id,@permission_mode=name 
	from base_permission_mode WITH ( NOLOCK )
	where is_default = 1
      and active = 1
      and org_id = @org_id
      and product_id = @product_id;
    
    IF @permission_mode IS NULL 
        RAISERROR ('No default permission mode found!',16,1) ;               
    SELECT  @count = COUNT(*)
    FROM    cache_user_product_permission
    WHERE   user_id = @user_id
            AND org_id = @org_id
            AND product_id = @product_id
            AND permission_mode = @permission_mode
    IF @count = 0 
        BEGIN
            EXECUTE usp_GenerateUserProductPermissionCache @user_id, @org_id,
                @product_id
        END
   
   
	DECLARE @system_list NVARCHAR(MAX);
	set @system_list='';
	DECLARE systemList_cursor cursor local for
	SELECT system_list from base_system_maintenance sm,base_system_group sg 
	WHERE sg.id=sm.system_group_id
		and	maintenance_category='SystemGroup'
		and sm.status='UnderMaintenance'
		and sm.maintenance_role_id not in (select * from dbo.GetRoleIDFromUserID(@user_id))
		and sm.org_id=@org_id
		and sm.product_id=@product_id ;
	open systemList_cursor
	declare @systems nvarchar(max)

	fetch next from systemList_cursor into @systems
	while @@FETCH_STATUS=0
	begin
	set @system_list=@system_list+@systems
	fetch next from systemList_cursor into @systems
	end

	close systemList_cursor
	deallocate systemList_cursor;
	
	with FunctionTree As(select * from(
    SELECT  distinct 
			[user_id],
			[user_name],
			org_id,
			org_name,
			product_id,
			product_name,
			permission_mode_id,
			permission_mode,
			domain_id,
			domain_name,
			domain_sort_code,
			system_id,
			system_name,
			system_description,
			instance_name,
			system_type,
			system_sort_code,
			function_id,
			parent_function_id,
			node_level_code,
			' ' As tree,
			node_type,
			function_code,
			function_name,
			function_description,
			assembly,
			form_class,
			navigate_url,
			external_navigate_url,
			target,
			instance_type,
			is_public,
			system_icon_name,
			custom_icon_url,
			function_sort_code
    FROM    cache_user_product_permission
    WHERE   user_id = @user_id
            AND org_id = @org_id
            AND product_id = @product_id
            AND permission_mode = @permission_mode
    --ORDER BY domain_name ,
    --        system_name ,
    --        node_level_code
/*
    UNION
    SELECT @user_id as user_id,
		   @user_name as user_name,
		   o.id as org_id,
		   o.name as org_name,
		   p.id as product_id,
		   p.name as product_name,
		   pm.id as permission_mode_id,
		   pm.name as permission_mode,
		   d.id as domain_id,
		   d.name as domain_name,
		   d.sort_code as domain_sort_code,
		   s.id as system_id,
		   s.name as system_name,
		   s.description as system_description,
		   s.instance_name,
		   s.system_type,
		   s.sort_code as system_sort_code,
		   f.id as function_id,
		   f.parent_function_id,
		   f.node_level_code,
		   space(dbo.GetCharNumber(f.node_level_code,'.')*4)+f.name tree,
		   f.node_type,
		   f.code as function_code,
		   f.name as function_name,
		   f.description as function_description,
		   f.assembly,
		   f.form_class,
		   isnull((select f.navigate_url where f.navigate_url like 'http://%' or f.navigate_url like 'https://'),s.base_url+f.navigate_url) navigate_url,
		   isnull((select f.navigate_url where f.navigate_url like 'http://%' or f.navigate_url like 'https://'),s.external_base_url+f.navigate_url) external_navigate_url,
		   f.target,
		   f.instance_type,
		   f.is_public,
		   f.system_icon_name,
		   f.custom_icon_url,
		   f.sort_code as function_sort_code
	FROM   base_permission perm,
		   base_role r,
			base_function f,
			base_system s,
			base_org o,
			base_domain d,
			base_product p,
			base_permission_mode pm
	WHERE	perm.subject_category='Role'
		and	perm.subject_id=r.id
		and perm.resource_category='Function'
		and perm.resource_id=f.id
		and s.id=f.system_id
		and o.id=s.org_id
		and d.id=s.domain_id
		and p.id=d.product_id
		and pm.id=perm.permission_mode_id
		and perm.active=1
		and r.active=1
		and f.active=1
		and s.active=1
		and o.active=1
		and d.active=1
		and p.active=1
		and pm.active=1
		and r.name='Everyone'
		and o.id=@org_id
		and p.id=@product_id
*/
	UNION
	SELECT	@user_id as user_id,
			@user_name as user_name,
			o.id as org_id,
			o.name as org_name,
			p.id as product_id,
			p.name as product_name,
			@permission_mode_id,
			@permission_mode,
			d.id as domain_id,
			d.name as domain_name,
			d.sort_code as domain_sort_code,
			s.id as system_id,
			s.name as system_name,
			s.description as system_description,
			s.instance_name,
			s.system_type,
			s.sort_code as system_sort_code,
			f.id as function_id,
			f.parent_function_id,
			f.node_level_code,
			' ' As tree,--space(dbo.GetCharNumber(f.node_level_code,'.')*4)+f.name tree,
			f.node_type,
			f.code as function_code,
			f.name as function_name,
			f.description as function_description,
			f.assembly,
			f.form_class,
			isnull((select f.navigate_url where f.navigate_url like 'http://%' or f.navigate_url like 'https://'),s.base_url+f.navigate_url) navigate_url,
			isnull((select f.navigate_url where f.navigate_url like 'http://%' or f.navigate_url like 'https://'),s.external_base_url+f.navigate_url) external_navigate_url,
			f.target,
			f.instance_type,
			f.is_public,
			f.system_icon_name,
			f.custom_icon_url,
			f.sort_code as function_sort_code
	FROM	base_function f,
			base_system s,
			base_org o,
			base_domain d,
			base_product p
	WHERE	s.id=f.system_id
		and o.id=s.org_id
		and d.id=s.domain_id
		and p.id=d.product_id
		and f.active=1
		and s.active=1
		and o.active=1
		and d.active=1
		and p.active=1
		and f.is_public=1
		and o.id=@org_id
		and p.id=@product_id
	--ORDER BY domain_name,
	--		 system_name,
	--		 node_level_code
	) funs    	
	where system_name not in
		(select distinct s.name as system_name from base_system_maintenance sm,base_system s
		where s.id=sm.system_group_id
		and maintenance_category='System'
		and status='UnderMaintenance'
		and maintenance_role_id not in (select * from dbo.GetRoleIDFromUserID(@user_id))
		and sm.org_id=@org_id
		and sm.product_id=@product_id
		union
		select distinct * from dbo.Split(@system_list,','))
     ), res as(    
        select * from FunctionTree a
        where parent_function_id is null            
        union all
        select d.* from FunctionTree d
        inner join res
        on d.parent_function_id=res.function_id)
     select * from res
        order by domain_name,
			 system_name,
			 node_level_code
    RETURN
