CREATE PROCEDURE [dbo].[usp_HasFunctionPermissionByOrg]
(@user_id UNIQUEIDENTIFIER,@org_id UNIQUEIDENTIFIER,@product_id UNIQUEIDENTIFIER, @system_name nvarchar(100),@url nvarchar(1024))
As
begin
 
    declare 
        @count  int,
        @permission_mode nvarchar(50)
    
    select @permission_mode=name from base_permission_mode 
        where is_default=1 and active=1 
            and org_id=@org_id and product_id=@product_id
    
    select @count=count(*) from cache_user_product_permission
        where user_id=@user_id
         and org_id=@org_id
         and product_id=@product_id         
         and permission_mode=@permission_mode
    if @count=0
    begin
        EXECUTE usp_GenerateUserProductPermissionCache @user_id,@org_id,@product_id
    end    
    select @count=count(*) from cache_user_product_permission
        where user_id=@user_id      
         and org_id=@org_id
         and product_id=@product_id   
         and system_name=@system_name
         and permission_mode=@permission_mode 
         and navigate_url like '%'+@url+'%'
    if @count=0
        set @count= dbo.IsSystemAdminBySystemName(@user_id, @org_id, @product_id, @system_name)
    else
        set @count=1
    return @count
end
