CREATE PROCEDURE [dbo].[usp_Tool_GetSQL]

(

    @tablename sysname,  

    @aimtablename  sysname = '',  

    @condition  nvarchar(500)  = '',  

    @showSql char(1)   = 'N'  

)

AS

declare @sqlName varchar(8000)  

declare @sqlValues varchar(8000)  

declare @strCondition varchar(1000)  

 

SET NOCOUNT ON  

 

select @sqlName =' ('  

select @sqlValues = 'VALUES (''+'  

if(@aimtablename = '')  

    select @aimtablename = @tablename

 

SELECT @sqlValues = @sqlValues + ColValue + ' + '','' + ', 

      @sqlName = @sqlName + '[' + ColName + '],'

FROM (SELECT CASE WHEN xtype IN (48, 52, 56, 59, 60, 62, 104, 106, 108, 122, 127) 

              THEN 'case when [' + name + '] is null then ''NULL'' else ' + 'cast([' + name + '] as varchar)'

               + ' end' WHEN xtype IN (58, 61) 

              THEN 'case when [' + name + '] is null then ''NULL'' else ' + ''''''''' + ' + 'convert(nvarchar(24),['

               + name + '],121)' + '+''''''''' + ' end' WHEN xtype IN (167, 175) 

              THEN 'case when [' + name + '] is null then ''NULL'' else ' + '''N'''''' + ' + 'replace(['

               + name + '],'''''''','''''''''''')' + '+''''''''' + ' end' WHEN xtype in(36)

                THEN 'case when [' + name + '] is null then ''NULL'' else '+'''''''''+convert(char(36),'+name+')+'''''''''+ ' end' WHEN xtype IN (231, 239) 

              THEN 'case when [' + name + '] is null then ''NULL'' else ' + '''N'''''' + ' + 'replace(['

               + name + '],'''''''','''''''''''')' + '+''''''''' + ' end' ELSE '''NULL''' END AS ColValue, 

              name AS ColName

        FROM syscolumns

        WHERE id = object_id(@tablename)) T  

 

 

select @sqlValues = left(@sqlValues,len(@sqlValues)-4)  

if(@showSql='Y')  

BEGIN  

    print '--SQL1 - GenColoums:'  

    print '--select ''INSERT INTO ['+ @aimtablename + ']' + left(@sqlName,len(@sqlName)-1)+') ' + @sqlValues + ')'' AS INSERTSQL from '+@tablename + space(1) + @condition  

    print '--SQL2 - Not GenColoums:'  

    print '--select ''INSERT INTO ['+ @aimtablename + '] ' +  @sqlValues + ')'' AS INSERTSQL from '+@tablename  + space(1) + @condition   

END

 

select @sqlName = left(@sqlName,len(@sqlName)-1)  

select @strCondition=replace(@condition,'''','''''')  

exec('SELECT ''--['+@tablename+']-->['+@aimtablename+']''  as [ ] UNION ' +   

'SELECT ''DELETE FROM ['+ @tablename + '] ' + @strCondition+''' as [ ] UNION ' +   

'SELECT ''INSERT INTO ['+ @aimtablename + ']' + @sqlName +') '+ @sqlValues + ')'' as [ ]  from '+ @tablename  + ' '  + @condition)  

 

SET NOCOUNT OFF


GO
